package day5

import org.scalatest.flatspec.AnyFlatSpec

class Puzzle1Test extends AnyFlatSpec {
  behavior of "process part 1"
  it should "handle no op" in {
    val program = Array(99)
    assert(Puzzle1.process(program, 0) == (program, Seq.empty))
  }

  def testWithNewProgram(source: Array[Int], input: Seq[Int], expectedProgram: Array[Int], expectedOutput: Seq[Int]): Unit = {
    val (resultingProgram, actualOutput) = Puzzle1.process(source.clone(), 0, input)
    assert(resultingProgram.toIndexedSeq == expectedProgram.toIndexedSeq)
    assert(actualOutput == expectedOutput)
  }

  def testOutputOnly(source: Array[Int], input: Seq[Int], expectedOutput: Seq[Int]): Unit = {
    val (_, actualOutput) = Puzzle1.process(source.clone(), 0, input)
    assert(actualOutput == expectedOutput)
  }

  it should "handle simple math" in {
    testWithNewProgram(Array(1, 0, 0, 0, 99), Seq.empty, Array(2, 0, 0, 0, 99), Seq.empty)
    testWithNewProgram(Array(2, 3, 0, 3, 99), Seq.empty, Array(2, 3, 0, 6, 99), Seq.empty)
  }

  it should "echo input to output" in {
    testWithNewProgram(Array(3, 0, 4, 0, 99), Seq(123), Array(123, 0, 4, 0, 99), Seq(123))
  }

  it should "handle modes" in {
    testWithNewProgram(Array(1002, 4, 3, 4, 33), Seq.empty, Array(1002, 4, 3, 4, 99), Seq.empty)
    testWithNewProgram(Array(104, 321, 99), Seq.empty, Array(104, 321, 99), Seq(321))
  }

  it should "handle negative integers" in {
    testWithNewProgram(Array(1101, 100, -1, 4, 0), Seq.empty, Array(1101, 100, -1, 4, 99), Seq.empty)
  }

